import {BrowserRouter as Router, Routes, Route} from 'react-router-dom'
import './App.css';
import Home from './components/Home/Home';
import Blog from './components/Blog/Blog';
import Navbar from './components/Navbar/Navbar';
import Pens from './components/Pens/Pens';
import Pencils from './components/Pencils/Pencils';
import ContactUs from './components/ContactUs/ContactUs';
import Cart from './components/Cart'; 
import Context from './store/Context';
import Product from './components/Product'

import React, {useState, useContext, useEffect} from "react";
import { ProductList } from './components/AppStyles';

function App() {
  const [isToggle, setToggle] = useState(false);
  const context = useContext(Context);

  useEffect(() =>{
    console.log(context);
  }, [context]);

  return (
    <div className="App">
      <Cart isToggle = {isToggle} setToggle={setToggle}/>
      <Router>
        <Navbar />
        <Routes >
          { <Route exact path='/' element={<Home />} /> }
          <Route exact path='/blog' element={<Blog />} />
          <Route exact path='/pens' element={<Pens />} />
          <Route exact path='/pencils' element={<Pencils />} />
         <Route exact path='/contactus' element={<ContactUs />} />
        </Routes>
      </Router>

      <div>
       <ProductList>
         {context.products.map((p) => (
           <Product key={p.id}>
            id={p.id}
            imageURL={p.imageURL}
            price = {p.price}
            title={p.title}
            addProductToCart={context.addProductToCart}
           </Product>
         ))}
       </ProductList>
     </div>

    </div>

  );
}

export default App;


