import styled from 'styled-components';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';


export const Wrapper = styled.div`
    display: block;
    margin: 0 5px;
    cursor: pointer;
    position: relative;

`;

export const Icon = styled.img`
    padding: 5px;
    width: 20px
    font-size: 24px;
    color: #ffffff;
`;

export const CartCount = styled.span`
    position: absolute;
    bottom: -5px;
    right: -5px;
    padding: 3px 7px;
    border-radius: 50px;
    background: #f05454;
    color: #ffffff;
    font-size: 12px;
    font-weight: 600;
`

export const CartSideBar = styled.div`
    z-index: 1000;
    width: 350px;
    background: #fffffff;
    top: 0;
    right: 0;
    padding: 25px;
    position: fixed;
    overflow: auto;
    height: 100%
    transtion: all ease-in-out 0.3s;

    &.expand{
        transition: all ease-in-out 0.3s;
        right: 0;
    }

    &.shrink{
        transition: all ease-in-out 0.3s;
        right: -400px;
    }

`
