import { Button } from "bootstrap";
import {useTranslation} from 'react-i18next';



function Confirmation(){
    const {t, i18n} = useTranslation();

    function handleClick(lang){
        console.log('im working')
        i18n.changeLanguage(lang);
    }



    return(
        <>
        <button onClick={()=>handleClick('en')}>English</button>
        <button onClick={()=>handleClick('ko')}>Korean</button>
        <button onClick={()=>handleClick('chi')}>Chinese</button>

        <div className="container-fluid">
            <div className="card">
                <h1 className="display-2 text-center  text-black ">{t('Thanks.1')}</h1>
                <br />
                <p className="card-body">An text has been sent to your phone number with your receipt! You will also be notified when your order has been shipped.<br />If you have any questions or concerns, please email us at isshinbenkyopens@gmail.com.

                    <br></br>
                    <a className="btn btn-success" href="index.html" role="button">Return to Home </a>


                </p>



            </div>
        </div></>


    )

}

export default Confirmation;
