import { Button } from 'bootstrap';
import React, {useState} from 'react';
import{Wrapper, OverlayWrapper, Card, CardBody, CardImage, CardPrice, cardTitle, CardTitle, CardButton,}from "./Styles";

export default function Product({addProductToCart, ...props}){


    return(
       <Wrapper>
        <CardButton onClick={() => addProductToCart({...props})}>Add to Cart</CardButton>
           <Card>
                <CardImage src={props.imageURL}/>
                <CardBody>
                    <CardTitle>{props.title}</CardTitle>
                    <CardPrice>{props.price}</CardPrice>

                </CardBody>
           </Card>
       </Wrapper>);

}
